function menuAnimate() {
  var navToggle = document.querySelector('.navigation__toggle');
  var menu = document.querySelector('.menu');
  navToggle.classList.toggle('open');
  menu.classList.toggle('show');
}

function bgChange() {
  var checkBox = document.getElementById("switch__input");
  var body = document.querySelector("body");

  if (checkBox.checked == true) {
    body.classList.add('play-mode');
  } else {
    body.classList.remove('play-mode');
  }
}

function flipCard() {
  var card = document.querySelector('.card');
  var leftButton = document.querySelector('.sound__button');
  var rightButton = document.querySelector('.info__button');
  card.classList.toggle('flipped');
  rightButton.classList.toggle('hidden');
  leftButton.classList.toggle('hidden');
}

function returnCard() {
  var card = document.querySelector('.card');

  if (card.classList.contains('flipped')) {
    setTimeout(flipCard(), 1000);
  }
}

function playSound() {
  var leftButton = document.querySelector('.sound__button');
  var word = leftButton.dataset.word;
  var url = "https://wooordhunt.ru/data/sound/word/uk/mp3/".concat(word, ".mp3");
  new Audio(url).play();
  console.log(url);
}

